export default {
	getRoleName: (roleId) => {
		switch(roleId){
			case 'frontend': 
				return {text: 'Frontend', color: '#FF731D'}
			case 'backend': 
				return {text: 'Backend', color: '#5F9DF7'}
			case 'devops': 
				return {text: 'DevOps', color: '#905E96'}
			case 'qa': 
				return {text: 'QA', color: '#367E18'}
			case 'fullstack': 
				return {text: 'Fullstack', color: '#874C62'}
			default: 
				return {text: 'Unknown', color: '#000000'}
		}
	},
	getStatusName: (statusId) => {
		switch(statusId){
			case 'new': 
				return {text: 'New', color: '#FFE9A0'}
			case 'shortlisted': 
				return {text: 'Shortlisted', color: '#E1FFB1'}
			case 'round1_done': 
				return {text: 'Round 1 Done', color: '#F3E0B5'}
			case 'round2_done': 
				return {text: 'Round 2 Done', color: '#FECD70'}
			case 'round3_done': 
				return {text: 'Round 3 Done', color: '#E3C770'}
			case 'selected': 
				return {text: 'Selected', color: '#90C8AC'}
			case 'joined': 
				return {text: 'Joined', color: '#CDF0EA'}
			case 'rejected': 
				return {text: 'Rejected', color: '#FFE3E1'}
			default: 
				return {text: 'Unknown', color: '#F9F9F9'}
		}
	},
	getPrimaryActionText: (applicant) => {
		switch(applicant.status){
			case 'new':
				return 'Shortlist'
			case 'shortlisted':
				return 'Proceed to Round 1'
			case 'round1_done':
				return 'Proceed to Round 2'
			case 'round2_done':
				return 'Send Offer'
			case 'offered':
				return 'Onboard'
			default: 
				return 'Error'
		}
	}
}
