actionConfiguration.body:
async () => {
  let applicant = appsmith.store.selected;
  if (applicant) {
    const payload = {
      ...applicant,
      status: 'rejected'
    };
    storeValue('rejectApplicant', payload);
    await reject_applicant.run().then(() => {
      storeValue('rejectApplicant', undefined);
      showAlert("Applicant Rejected");
    }).catch(() => {
      showAlert("Unable to process");
    });
  }
}

