actionConfiguration.body:
statusId => {
  switch (statusId) {
    case 'new':
      return {
        text: 'New',
        color: '#FFE9A0'
      };
    case 'shortlisted':
      return {
        text: 'Shortlisted',
        color: '#E1FFB1'
      };
    case 'round1_done':
      return {
        text: 'Round 1 Done',
        color: '#F3E0B5'
      };
    case 'round2_done':
      return {
        text: 'Round 2 Done',
        color: '#FECD70'
      };
    case 'round3_done':
      return {
        text: 'Round 3 Done',
        color: '#E3C770'
      };
    case 'selected':
      return {
        text: 'Selected',
        color: '#90C8AC'
      };
    case 'joined':
      return {
        text: 'Joined',
        color: '#CDF0EA'
      };
    case 'rejected':
      return {
        text: 'Rejected',
        color: '#FFE3E1'
      };
    default:
      return {
        text: 'Unknown',
        color: '#F9F9F9'
      };
  }
}

